import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  centerFormulario: {
    flex: 1,
    backgroundColor: 'black', // Fondo negro
    alignItems: 'center',
    justifyContent: 'center',
  },

  grande: {
    fontSize: 34,
    color: 'white', // Texto blanco
  },

  observacionGana: {
    color: 'green',
  },

  observacionPierde: {
    color: 'red',
  },

  observacionHabilita: {
    color: 'orange',
  },
});

export default styles;
