import { StyleSheet } from 'react-native';

export default StyleSheet.create({

  centerFormulario: {
    flex: 1, // Ocupa todo el espacio disponible
    alignItems: 'center', // Centra horizontalmente
    justifyContent: 'center', // Centra verticalmente
    fontSize: 20
  },

  // Estilo para hacer el título más grande
  grande: {
    fontSize: 34, // Puedes ajustar el tamaño como desees
  },

  // Estilo para la observación "Gana" (verde)
  observacionGana: {
    color: 'green',
  },

  // Estilo para la observación "Pierde" (rojo)
  observacionPierde: {
    color: 'red',
  },

  // Estilo para la observación "Habilita" (naranja)
  observacionHabilita: {
    color: 'orange',
  },
});
