const styles = StyleSheet.create({
  // ... (otros estilos)
  
  // Estilo para el fondo negro y letras blancas
  pantallaPrincipal: {
    flex: 1,
    backgroundColor: 'black', // Fondo negro
    alignItems: 'center',
    justifyContent: 'center',
  },

  // Estilo para el texto en blanco y tamaño grande
  textoBlancoGrande: {
    color: 'white', // Texto blanco
    fontSize: 34, // Tamaño de letra grande
  },
});

export default styles;
