import { StyleSheet } from 'react-native';

export default StyleSheet.create({
  // Otros estilos aquí...

  // Estilo para centrar elementos
  center: {
    alignItems: 'center',
    justifyContent: 'center',
  },

  // Estilo para hacer el título más grande
  grande: {
    fontSize: 24, // Puedes ajustar el tamaño como desees
  },

  // Estilo para la observación "Gana" (verde)
  observacionGana: {
    color: 'green',
  },

  // Estilo para la observación "Pierde" (rojo)
  observacionPierde: {
    color: 'red',
  },

  // Estilo para la observación "Habilita" (naranja)
  observacionHabilita: {
    color: 'orange',
  },
});
